states.controller:
function($stateParams,$http,$sce,$window,$timeout){
    var vm = this;
    var base = $stateParams.base;

    vm.openTab = 0;
    vm.$sce = $sce;

    vm.selectCourse = selectCourse;
    vm.getHtml = getHtml;

    init();

    function init(){

        if ($stateParams.hash) {
            ipfs.cat("/ipfs/"+$stateParams.hash, function (err, file) {
                if (err) {
                    throw err
                }
            //   console.log(file.toString('utf8'))
                vm.course = JSON.parse((file.toString('utf8')))
                vm.currentLessonId = window.localStorage['currentLessonId'];
                vm.openTab = window.localStorage['currentTab'];
                vm.currentLessonIndex = window.localStorage['currentLessonIndex'];
                vm.lesson = vm.course.parts[vm.openTab].lessons[vm.currentLessonIndex];

                $timeout(function(){})
            })
        }
        else if ($stateParams.index){
            vm.course = $window.courses[$stateParams.index];
            vm.currentLessonId = window.localStorage['currentLessonId'];
            vm.openTab = window.localStorage['currentTab'];
            vm.currentLessonIndex = window.localStorage['currentLessonIndex'];
            vm.lesson = vm.course.parts[vm.openTab].lessons[vm.currentLessonIndex];

            $timeout(function(){})
        }
        else if (!$window.courses || !$window.courses[0]){
            $state.go("app.entrance")
        }
        else {
            vm.course = $window.courses[0];
            vm.currentLessonId = window.localStorage['currentLessonId'];
            vm.openTab = window.localStorage['currentTab'];
            vm.currentLessonIndex = window.localStorage['currentLessonIndex'];
            vm.lesson = vm.course.parts[vm.openTab].lessons[vm.currentLessonIndex];
        }


    }

    function selectCourse(partIndex,lessonIndex){

        vm.currentLessonId = partIndex + '.' + lessonIndex;

        window.localStorage['currentLessonId'] = partIndex + '.' + lessonIndex;
        window.localStorage['currentLessonIndex'] = lessonIndex;
        window.localStorage['currentTab'] = partIndex;


        vm.lesson = vm.course.parts[partIndex].lessons[lessonIndex];

        console.log(vm.lesson)




    }

    function getHtml(text){
        var converter = new showdown.Converter();
        var html = converter.makeHtml(text);
        return vm.$sce.trustAsHtml(html);
    }
}

