function($state,$stateParams,$window,$scope,$interval,$mdToast){
    var vm = this;

    vm.state = $state.current.name;


    if ($stateParams.id){
        $window.selectedCourse = $window.courses[$stateParams.id];
    }
    else {
        $window.selectedCourse = $window.courses[0];
    }

    vm.selectedCourse = $window.selectedCourse


    vm.navs = [
        {
            state:"app.course-edit.detail",
            icon:"details",
            name:"Info"
        },
        {
            state:"app.course-edit.course",
            icon:"book",
            name:"Course"
        },
        {
            state:"app.course-edit.export",
            icon:"import_export",
            name:"Export"
        },
        {
            state:"app.course-edit.publish",
            icon:"cloud",
            name:"Publish"
        }
    ]

    vm.goto = function(state){
        vm.state = state;
        $state.go(state);
    }

    vm.preview = function(){
        vm.previewWindow = window.open("https://thebuilder.hk/board/web/?project_id=1TzGkkrJqV#!/app/course?index="+$stateParams.id)
    }

    // vm.courseChange = function(){
    //     console.log($window.selectedCourse);
    //     console.log(vm.selectedCourse)
    //     $state.go("app.course-edit.course",{id:vm.courses.indexOf(vm.selectedCourse)})
    // }

    vm.save = function(){
        $window.localStorage.courses = JSON.stringify($window.courses);
        $mdToast.show(
          $mdToast.simple()
            .textContent('Course Saved')
            .position("bottom right")
            .hideDelay(2000)
        );
        if (vm.previewWindow){
            vm.previewWindow.location.reload();
        }
    }


    vm.saveInterval = $interval(function(){
        $window.localStorage.courses = JSON.stringify($window.courses);
        $mdToast.show(
          $mdToast.simple()
            .textContent('Course Auto-Saved')
            .position("bottom right")
            .hideDelay(2000)
        );
    },20000)

    $scope.$on("$destroy",function(){
        $interval.cancel(vm.saveInterval)
    })
}

