function($window,$timeout){

    var vm = this;

    vm.course = $window.selectedCourse

    vm.publish = function(){
        ipfs.add(ipfs.Buffer(JSON.stringify(vm.course))).then(function(result){

        	if (vm.course.versions) {
                vm.course.versions.push({
                    createdAt:new Date(),
                    hash:result[0].hash
                })
            }
            else {

                vm.course.published = true;

                vm.course.versions = [{
                    createdAt:new Date(),
                    hash:result[0].hash
                }]
            }

            $window.localStorage.courses = JSON.stringify($window.courses);
        	$timeout(function(){})
        })
        .catch(function(err){
        	console.log(err)
        })


    }
}

