function($stateParams,$http,$sce){
    var vm = this;
    var base = $stateParams.base;

    vm.openTab = 0;
    vm.$sce = $sce;

    vm.selectCourse = selectCourse;
    vm.getHtml = getHtml;

    init();

    function init(){



        if (base){
            $http.get(base+"/course.json").then(function(response){
                vm.course = response.data.data.nanodegree;

                var currentId = window.localStorage['currentCourseId'] || vm.course.parts[0].modules[0].lessons[0].id;

                vm.openTab = window.localStorage['currentTab'] || 0;
                vm.selectCourse(currentId)

            })
        }


    }

    function selectCourse(id,part){


        if (vm.currentCourseId == id){
            return;
        }

        vm.currentCourseId = id;

        window.localStorage['currentCourseId'] = id;

        if (part){
            window.localStorage['currentTab'] = vm.course.parts.indexOf(part);
        }


        $http.get(base+"/lesson/"+id+".json").then(function(response){
            // var converter = new showdown.Converter();
            // var text      = '# hello, markdown!';
            // vm.lesson = converter.makeHtml(response.data.data.lesson);

            vm.lesson = response.data.data.lesson;
        })



    }

    function getHtml(text){
        var converter = new showdown.Converter();
        var html = converter.makeHtml(text);
        return vm.$sce.trustAsHtml(html);
    }
}

